import { createPost } from '../api';
import React, { useState } from 'react';
import { Navigate } from 'react-router-dom';
import { useParams } from 'react-router-dom';




const EditPost = ({ posts }) => {
    const { postID } = useParams();

    const [currentPost] = posts.filter(post => post._id === postID);

    const {title, description, location, price, willDeliver} = currentPost;


 const [newtitle, setNewTitle] = useState(title);
const [newdescription, setNewDescription] = useState(description);
const [newprice, setNewPrice] = useState(price);
const [newlocation, setNewLocation] = useState(location);
const [newwillDeliver, setNewWillDeliver] = useState(willDeliver);
  const newPost = {
    newtitle,
    newdescription,
    newprice,
    newlocation,
    newwillDeliver
  }




  return (
    // This needs to be a form that accepts the 5 request parameters for creating a post
    <>
     <form  class='form' onSubmit={(event) => {
      event.preventDefault();
    }}>
    <input 
      class="createPost"
        type='text'
        placeholder={title}
        onChange={(event) => setNewTitle(event.target.value)}
      />
       <input 
      class="createPost"
      id='description'
        type='text'
        placeholder={description}
        onChange={(event) => setNewDescription(event.target.value)}
      />
       <input 
      class="createPost"
        type='text'
        placeholder={price}
        onChange={(event) => setNewPrice(event.target.value)}
      />
       <input 
      class="createPost"
        type='text'
        placeholder={location}
        onChange={(event) => setNewLocation(event.target.value)}
      />
       <input 
      class="createPost"
        type='checkbox'
        checked={willDeliver}
        onChange={(event) => setNewWillDeliver(event.target.checked)}
      />
    <button type="submit">Create a New Post</button>
    </form>
    </>
  )
}

export default EditPost;
