import React, {useState} from 'react';
import { useParams } from 'react-router-dom';
import { Link } from 'react-router-dom';
import { createMessage } from '../api';

const SendMessage = ({postID,token}) => {
  const[message, setMessage] = useState({content:''});

  async function addMessage(){
 await createMessage({postID, message, token})

  }

  return(
    <div>
       <form onSumbmit={(event)=>{
         event.preventDefault();
        addMessage();
        }}>
         <input 
        class="createMessage"
          type='text'
          placeholder='Message'
          onChange={(event) => setMessage({content: event.target.value})}
        />
        <button type='submit'>Send Message</button>
        </form>
    </div>
  )
}

const SinglePostView = ({ posts,token }) => {
  const [activateMessage,setActivateMessage]= useState(false);
  const { postID } = useParams();

  const [currentPost] = posts.filter(post => post._id === postID);

  const {title, description, location, price, willDeliver} = currentPost;

  return (
    <div>
    <div>
      <h3>{title}</h3>
      <p>Description: {description}</p>
      <p>Price: {price}</p>
      <p>Location: {location}</p>
      <p>Will Deliver: {willDeliver}</p>
    </div>
   <button onClick={()=> setActivateMessage(!activateMessage)}>Send Message</button>
    <Link id="return" to='/posts'>Return</Link>
    {activateMessage && <SendMessage postID={postID} token={token}/>}
   </div>
  )
}

export default SinglePostView;
